import React, { useEffect, useState } from "react";
import axios from "axios";

import AddAccountForm from "./addAccount";
import AccountList from "./AccountList";
import SharedAccounts from "./SharedAccounts";
import Settings from "./Settings";

import "../styles/MainLayout.css";

function Home() {
  const [accounts, setAccounts] = useState([]);
  const [activeSection, setActiveSection] = useState("personel");
  const [editingAccount, setEditingAccount] = useState(null);

  // Backend'den hesapları çek
  const fetchAccounts = () => {
    axios.get("http://localhost:5059/api/accounts")
      .then(res => setAccounts(res.data))
      .catch(err => console.error("Hesaplar alınamadı:", err));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  // Hesap silme işlemi
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5059/api/Accounts/${id}`)
      .then(() => fetchAccounts())
      .catch(err => console.error("Hesap silinemedi:", err));
  };

  // Düzenleme için seçilen hesabı ayarla
  const handleEdit = (account) => {
    setEditingAccount(account);
    setActiveSection("personel");  // Düzenleme formu personel sekmesinde açılsın
  };

  // Form gönderildiğinde güncelle
  const handleFormSubmit = () => {
    setEditingAccount(null);
    fetchAccounts();
  };

  // Butonlar için ortak stil fonksiyonu
  const buttonStyle = (section) => ({
    background: activeSection === section ? "#007bff" : "transparent",
    color: activeSection === section ? "white" : "black",
    padding: "10px",
    border: "none",
    cursor: "pointer",
    textAlign: "left",
  });

  return (
    <div className="home-container" style={{ display: "flex", height: "100vh" }}>

      {/* Sol Menü */}
      <nav style={{
        width: "220px",
        borderRight: "1px solid #ccc",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        gap: "10px"
      }}>
        <button onClick={() => setActiveSection("personel")} style={buttonStyle("personel")}>
          Personel
        </button>
        <button onClick={() => setActiveSection("paylasilanlar")} style={buttonStyle("paylasilanlar")}>
          Paylaşılanlar
        </button>
        <button onClick={() => setActiveSection("hesapYonetim")} style={buttonStyle("hesapYonetim")}>
          Hesap Bilgileri Yönetim Sayfası
        </button>
        <button onClick={() => setActiveSection("ayarlar")} style={buttonStyle("ayarlar")}>
          Ayarlar Sayfası
        </button>
      </nav>

      {/* Sağ içerik */}
      <main style={{ flexGrow: 1, padding: "20px", overflowY: "auto" }}>
        {activeSection === "personel" && (
          <>
            <h2>Personel Hesaplar</h2>
            <AddAccountForm
              onAccountAdded={handleFormSubmit}
              editingAccount={editingAccount}
            />
            <AccountList
              accounts={accounts}
              onDelete={handleDelete}
              onEdit={handleEdit}
            />
          </>
        )}

        {activeSection === "paylasilanlar" && (
          <>
            <h2>Paylaşılan Hesaplar</h2>
            <SharedAccounts accounts={accounts} />
          </>
        )}

        {activeSection === "hesapYonetim" && (
          <>
            <h2>Hesap Bilgileri Yönetim Sayfası</h2>
            <p>Hesap yönetim fonksiyonları buraya gelecek.</p>
          </>
        )}

        {activeSection === "ayarlar" && (
          <>
            <h2>Ayarlar Sayfası</h2>
            <Settings />
          </>
        )}
      </main>
    </div>
  );
}

export default Home;
