import React from "react";
import "../styles/MainLayout.css";
function AccountList({ accounts, onDelete, onEdit }) {
  if (!accounts || accounts.length === 0) return <p>Hesap bulunamadı.</p>;

  return (
    <table style={{ width: "100%", borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ borderBottom: "1px solid #ccc" }}>
          <th>Platform</th>
          <th>Kullanıcı Adı</th>
          <th>Şifre</th>
          <th>Notlar</th>
          <th>Paylaşım</th>
          <th>İşlemler</th>
        </tr>
      </thead>
      <tbody>
        {accounts.map((acc) => (
          <tr key={acc.id} style={{ borderBottom: "1px solid #eee" }}>
            <td>{acc.platform}</td>
            <td>{acc.username}</td>
            <td>{acc.password}</td>
            <td>{acc.notes}</td>
            <td>{acc.isShared ? "Evet" : "Hayır"}</td>
            <td>
              <button onClick={() => onEdit(acc)} style={{ marginRight: "8px" }}>Düzenle</button>
              <button onClick={() => onDelete(acc.id)}>Sil</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default AccountList;
