import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

function Login() {
  const [step, setStep] = useState(1); // 1: Azure AD butonu, 2: kullanıcı adı formu
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleAzureLoginClick = () => {
    setStep(2);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Basit doğrulama (sonra backend bağla)
    if (username.trim() && password.trim()) {
      // Giriş başarılı kabul et
      navigate("/home");
    } else {
      alert("Lütfen kullanıcı adı ve şifre giriniz.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        {step === 1 && (
          <>
            <h1>NDAccountManager</h1>
            <button onClick={handleAzureLoginClick}>Azure AD ile Giriş Yap</button>
          </>
        )}
        {step === 2 && (
          <form onSubmit={handleSubmit}>
            <h2>Kullanıcı Girişi</h2>
            <input
              type="text"
              placeholder="Kullanıcı Adı"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Şifre"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">Giriş Yap</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Login;
