import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/MainLayout.css"; // varsa stil dosyan

function SharedAccounts() {
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5059/api/Accounts/shared")
      .then((res) => {
        setAccounts(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Paylaşılan hesaplar alınamadı:", err);
        setLoading(false);
      });
  }, []);

  return (
    <div className="account-list-container">
      <h2>Paylaşılan Hesaplar</h2>
      {loading ? (
        <p>Yükleniyor...</p>
      ) : accounts.length === 0 ? (
        <p>Hiçbir paylaşılan hesap yok.</p>
      ) : (
        <ul className="account-list">
          {accounts.map((account) => (
            <li key={account.id} className="account-item">
              <p><strong>Platform:</strong> {account.platform}</p>
              <p><strong>Kullanıcı Adı:</strong> {account.username}</p>
              <p><strong>Email:</strong> {account.email}</p>
              <p><strong>IP Adresi:</strong> {account.ipAddress}</p>
              <p><strong>Kategori:</strong> {account.category}</p>
              <p><strong>Notlar:</strong> {account.notes}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SharedAccounts;
