import React from "react";
import "../styles/Home.css";

function AccountList({ accounts, onDelete, onUpdate, canEdit }) {
  if (accounts.length === 0) return <p>Henüz hesap bulunmuyor.</p>;

  return (
    <div className="account-list">
      {accounts.map((acc) => (
        <div key={acc.id} className="account-card">
          <p><strong>Platform:</strong> {acc.platform}</p>
          <p><strong>Kullanıcı:</strong> {acc.username}</p>
          <p><strong>E-posta:</strong> {acc.email}</p>

          <p><strong>Kategori:</strong> {acc.category}</p>
          <p><strong>Not:</strong> {acc.notes}</p>
          {canEdit && (
            <div className="actions">
              <button onClick={() => onDelete(acc.id)}>Sil</button>
              <button
                onClick={() => {
                  const updated = {
                    ...acc,
                    notes: prompt("Yeni not girin:", acc.notes),
                  };
                  onUpdate(updated);
                }}
              >
                Güncelle
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default AccountList;
