import React, { useEffect, useState } from "react";
import axios from "axios";

import AddAccountForm from "./addAccount";
import AccountList from "./AccountList";
import SharedAccounts from "./SharedAccounts";
import Settings from "./Settings";

import "../styles/MainLayout.css";

function Home() {
  const [accounts, setAccounts] = useState([]);
  const [activeSection, setActiveSection] = useState("personel");
  const [editingAccount, setEditingAccount] = useState(null);

  const fetchAccounts = () => {
    axios.get("http://localhost:5059/api/Accounts")
      .then(res => setAccounts(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5059/api/Accounts/${id}`)
      .then(fetchAccounts)
      .catch(err => console.error(err));
  };

  const handleEdit = (account) => {
    setEditingAccount(account);
    setActiveSection("personel");  // düzenleme formu personel kısmında gösterilir
  };

  const handleFormSubmit = () => {
    setEditingAccount(null);
    fetchAccounts();
  };

  return (
    <div className="home-container" style={{ display: "flex", height: "100vh" }}>

      {/* Sol Menü */}
      <nav style={{
        width: "200px",
        borderRight: "1px solid #ccc",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        gap: "10px"
      }}>
        <button
          onClick={() => setActiveSection("personel")}
          style={{ background: activeSection === "personel" ? "#007bff" : "transparent", color: activeSection === "personel" ? "white" : "black", padding: "10px", border: "none", cursor: "pointer" }}
        >
          Personel
        </button>
        <button
          onClick={() => setActiveSection("paylasilanlar")}
          style={{ background: activeSection === "paylasilanlar" ? "#007bff" : "transparent", color: activeSection === "paylasilanlar" ? "white" : "black", padding: "10px", border: "none", cursor: "pointer" }}
        >
          Paylaşılanlar
        </button>
        <button
          onClick={() => setActiveSection("hesapYonetim")}
          style={{ background: activeSection === "hesapYonetim" ? "#007bff" : "transparent", color: activeSection === "hesapYonetim" ? "white" : "black", padding: "10px", border: "none", cursor: "pointer" }}
        >
          Hesap Bilgileri Yönetim Sayfası
        </button>
        <button
          onClick={() => setActiveSection("ayarlar")}
          style={{ background: activeSection === "ayarlar" ? "#007bff" : "transparent", color: activeSection === "ayarlar" ? "white" : "black", padding: "10px", border: "none", cursor: "pointer" }}
        >
          Ayarlar Sayfası
        </button>
      </nav>

      {/* Sağ içerik */}
      <main style={{ flexGrow: 1, padding: "20px", overflowY: "auto" }}>
        {activeSection === "personel" && (
          <>
            <h2>Personel Hesaplar</h2>
            <AddAccountForm
              onAccountAdded={handleFormSubmit}
              editingAccount={editingAccount}
            />
            <AccountList
              accounts={accounts}
              onDelete={handleDelete}
              onEdit={handleEdit}
            />
          </>
        )}

        {activeSection === "paylasilanlar" && (
          <>
            <h2>Paylaşılan Hesaplar</h2>
            <SharedAccounts accounts={accounts} />
          </>
        )}

        {activeSection === "hesapYonetim" && (
          <>
            <h2>Hesap Bilgileri Yönetim Sayfası</h2>
            {/* Buraya dilediğin başka bileşeni ekleyebilirsin */}
            <p>Hesap yönetim fonksiyonları buraya gelecek.</p>
          </>
        )}

        {activeSection === "ayarlar" && (
          <>
            <h2>Ayarlar Sayfası</h2>
            <Settings />
          </>
        )}
      </main>
    </div>
  );
}

export default Home;
