import React, { useEffect, useState } from "react";
import axios from "axios";
import AccountCard from "./AccountCard";
import AddAccountForm from "./addAccount"; // dosya adı küçük harf değil, büyük harf ile eşleşmeli
import "../styles/MainLayout.css";

function Home() {
  const [accounts, setAccounts] = useState([]);
  const [activeTab, setActiveTab] = useState("personel");
  const [editingAccount, setEditingAccount] = useState(null);

  // API'den hesapları getir
  const fetchAccounts = () => {
    axios.get("http://localhost:5059/api/Accounts")
      .then(res => setAccounts(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = id => {
    axios.delete(`http://localhost:5059/api/Accounts/${id}`)
      .then(fetchAccounts)
      .catch(err => console.error(err));
  };

  const handleEdit = (account) => {
    setEditingAccount(account);
  };

  const handleFormSubmit = () => {
    setEditingAccount(null);
    fetchAccounts();
  };

  return (
    <div className="home-container">
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active-tab" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilanlar" ? "active-tab" : ""}
          onClick={() => setActiveTab("paylasilanlar")}
        >
          Paylaşılanlar
        </button>
      </div>

      {activeTab === "personel" && (
        <>
          <h2>Personel Hesaplar</h2>
          <AddAccountForm
            onAccountAdded={handleFormSubmit}
            editingAccount={editingAccount}
          />
          <div className="account-list">
            {accounts.map((acc) => (
              <AccountCard
                key={acc.id}
                account={acc}
                onDelete={handleDelete}
                onEdit={handleEdit}
              />
            ))}
          </div>
        </>
      )}

      {activeTab === "paylasilanlar" && (
        <>
          <h2>Paylaşılan Hesaplar</h2>
          <table className="shared-accounts-table">
            <thead>
              <tr>
                <th>Paylaşan</th>
                <th>Hesap Adı</th>
                <th>Kullanıcı Adı</th>
                <th>Kategori</th>
                <th>Paylaşılan Kişiler</th>
              </tr>
            </thead>
            <tbody>
              {accounts
                .filter(acc => acc.isShared && acc.sharedWith && acc.sharedWith.length > 0)
                .map((acc) => (
                  <tr key={acc.id}>
                    {/* Eğer API'den 'sharedBy' yoksa bunu 'owner' veya username gibi uygun alanla değiştir */}
                    <td>{acc.sharedBy || acc.owner || "Bilinmiyor"}</td>
                    <td>{acc.platform || acc.name}</td>
                    <td>{acc.username}</td>
                    <td>{acc.category || "Belirtilmemiş"}</td>
                    <td>{acc.sharedWith.join(", ")}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default Home;
