import './Login.css';

import React, { useState } from "react";

import './Login.css';
export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    if(username.trim() !== "") {
      onLogin(username.trim());
    }
  }

  return (
    <div style={{maxWidth: 300, margin: "100px auto", padding: 20, border: "1px solid #ccc", borderRadius: 8}}>
      <h2 style={{color: "#FF6600"}}>Giriş Yap</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Kullanıcı Adı" 
          value={username} 
          onChange={e => setUsername(e.target.value)} 
          style={{width: "100%", padding: 8, marginBottom: 10}}
        />
        <button 
          type="submit" 
          style={{width: "100%", padding: 10, backgroundColor: "#FF6600", color: "white", border: "none", borderRadius: 4, cursor: "pointer"}}
        >
          Giriş
        </button>
      </form>
    </div>
  );
}
