import React, { useEffect, useState } from "react";
import { useMsal } from "@azure/msal-react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import Login from "./components/Login";
import GeneralHome from "./components/GeneralHome";
import ManagerHome from "./components/ManagerHome";
import SupportHome from "./components/SupportHome";
import { getUserRoleFromIdToken } from "./utils/authUtils";

const AppContent = () => {
  const { accounts } = useMsal();
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    if (accounts.length > 0) {
      const account = accounts[0];
      const role = getUserRoleFromIdToken(account.idTokenClaims);

      setUserRole(role);

      // Role göre yönlendirme
      switch (role) {
        case "Manager":
          navigate("/manager");
          break;
        case "Support":
          navigate("/support");
          break;
        default:
          navigate("/general");
          break;
      }
    }
  }, [accounts, navigate]);

  return (
    <Routes>
      <Route path="/" element={<Login />} />
      <Route path="/general" element={<GeneralHome />} />
      <Route path="/manager" element={<ManagerHome />} />
      <Route path="/support" element={<SupportHome />} />
    </Routes>
  );
};

const App = () => {
  return (
    <Router>
      <AppContent />
    </Router>
  );
};

export default App;
