import "../styles/MainLayout.css";
import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([
    { platform: "Gmail", username: "dilarayildirim@gmail.com", password: "*****" },
    { platform: "LinkedIn", username: "dilarayildirim", password: "*****" }
  ]);

  const [newAccount, setNewAccount] = useState({ platform: "", username: "", password: "" });

  const handleNewAccountChange = (e) => {
    const { name, value } = e.target;
    setNewAccount({ ...newAccount, [name]: value });
  };

  const handleAddAccount = (e) => {
    e.preventDefault();
    if (newAccount.platform && newAccount.username && newAccount.password) {
      setAccounts([...accounts, newAccount]);
      setNewAccount({ platform: "", username: "", password: "" });
    }
  };

  const handleFieldChange = (index, field, value) => {
    const updated = [...accounts];
    updated[index][field] = value;
    setAccounts(updated);
  };

  const handleUpdateAccount = (index) => {
    const account = accounts[index];
    console.log("Güncellenen hesap bilgisi:", account);
    alert("Hesap bilgileri güncellendi!");
    // Backend'e gönderilecekse buraya API çağrısı yazılır.
  };

  const renderContent = () => {
    if (activeTab === "personel") {
      return (
        <div>
          <h3>Kaydedilen Hesaplar</h3>
          <ul className="account-list">
            {accounts.map((acc, index) => (
              <li key={index}>
                <strong>{acc.platform}</strong> - {acc.username}

                <div className="update-account-form">
                  <input
                    type="text"
                    value={acc.platform}
                    onChange={(e) => handleFieldChange(index, "platform", e.target.value)}
                  />
                  <input
                    type="text"
                    value={acc.username}
                    onChange={(e) => handleFieldChange(index, "username", e.target.value)}
                  />
                  <input
                    type="password"
                    value={acc.password}
                    onChange={(e) => handleFieldChange(index, "password", e.target.value)}
                  />
                  <button onClick={() => handleUpdateAccount(index)}>Güncelle</button>
                </div>

                {/* Paylaşım Alanı */}
                <div className="share-options">
                  <label>Paylaşılacak Kişi Email:</label>
                  <input type="email" placeholder="ornek@kisi.com" />
                  <label>Süre:</label>
                  <select>
                    <option value="forever">Süresiz</option>
                    <option value="1day">1 Gün</option>
                    <option value="1week">1 Hafta</option>
                  </select>
                  <button>Paylaş</button>
                </div>

                <hr />
              </li>
            ))}
          </ul>

          <div className="add-account-form">
            <h4>Yeni Hesap Ekle</h4>
            <form onSubmit={handleAddAccount}>
              <input
                type="text"
                name="platform"
                placeholder="Platform Adı (ör: Gmail)"
                value={newAccount.platform}
                onChange={handleNewAccountChange}
              />
              <input
                type="text"
                name="username"
                placeholder="Kullanıcı Adı veya Email"
                value={newAccount.username}
                onChange={handleNewAccountChange}
              />
              <input
                type="password"
                name="password"
                placeholder="Şifre"
                value={newAccount.password}
                onChange={handleNewAccountChange}
              />
              <button type="submit">Kaydet</button>
            </form>
          </div>
        </div>
      );
    } else if (activeTab === "shared") {
      return <p>Buraya paylaşılan hesaplar gelecek...</p>;
    }
  };

  return (
    <div className="home-container">
      {/* Üst Menü */}
      <nav className="top-nav">
        <Link to="/account" className="nav-link">Hesap Bilgileri</Link>
        {userRole === "Manager" && (
          <Link to="/settings" className="nav-link">Ayarlar</Link>
        )}
      </nav>

      {/* Sekmeler */}
      <div className="tab-header">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "shared" ? "active" : ""}
          onClick={() => setActiveTab("shared")}
        >
          Paylaşılanlar
        </button>
      </div>

      <div className="tab-content">{renderContent()}</div>
    </div>
  );
}

export default Home;
