import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddAccountForm = ({ editingAccount, onAccountAdded }) => {
  const [formData, setFormData] = useState({
    platform: '',
    username: '',
    password: '',
    email: '',
    ipAddress: '',
    notes: ''
  });

  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  // Formu güncelleme verileriyle doldur
  useEffect(() => {
    if (editingAccount) {
      setFormData(editingAccount);
    }
  }, [editingAccount]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    try {
      if (editingAccount) {
        // Güncelleme
        await axios.put(`http://localhost:5059/api/accounts/${editingAccount.id}`, formData);
        setSuccessMessage("Hesap başarıyla güncellendi.");
      } else {
        // Yeni kayıt
        await axios.post('http://localhost:5059/api/accounts', formData);
        setSuccessMessage("Hesap başarıyla eklendi.");
      }

      onAccountAdded(); // Listeyi yenile
      setFormData({
        platform: '',
        username: '',
        password: '',
        email: '',
        ipAddress: '',
        notes: ''
      });
    } catch (error) {
      console.error('Kayıt işlemi hatası:', error);
      setError("Hesap kaydedilemedi. Lütfen tekrar deneyin.");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
      <input
        name="platform"
        value={formData.platform}
        onChange={handleChange}
        placeholder="Platform"
        required
      />
      <input
        name="username"
        value={formData.username}
        onChange={handleChange}
        placeholder="Kullanıcı Adı"
        required
      />
      <input
        type="password"
        name="password"
        value={formData.password}
        onChange={handleChange}
        placeholder="Şifre"
      />
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="E-posta"
      />
      <input
        name="ipAddress"
        value={formData.ipAddress}
        onChange={handleChange}
        placeholder="IP Adresi"
      />
      <input
        name="notes"
        value={formData.notes}
        onChange={handleChange}
        placeholder="Notlar"
      />
      <button type="submit">{editingAccount ? "Güncelle" : "Kaydet"}</button>

      {error && <div style={{ color: 'red' }}>{error}</div>}
      {successMessage && <div style={{ color: 'green' }}>{successMessage}</div>}
    </form>
  );
};

export default AddAccountForm;
