import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AddAccountForm({ onAccountAdded, editingAccount }) {
  const [formData, setFormData] = useState({
    platform: '',
    username: '',
    password: '',
    notes: '',
    isShared: false,
    sharedWithUserId: '',
  });
  const [users, setUsers] = useState([]);

  useEffect(() => {
    if (editingAccount) {
      setFormData(editingAccount);
    }
  }, [editingAccount]);

  useEffect(() => {
    axios.get('http://localhost:5059/api/Accounts')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Kullanıcılar alınamadı:', err));
  }, []);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    const url = editingAccount
      ? `http://localhost:5059/api/Accounts/${editingAccount.id}`
      : `http://localhost:5059/api/Accounts`;

    const method = editingAccount ? axios.put : axios.post;

    method(url, formData)
      .then(() => {
        onAccountAdded();
        setFormData({
          platform: '',
          username: '',
          password: '',
          notes: '',
          isShared: false,
          sharedWithUserId: '',
        });
      })
      .catch(err => console.error('Hesap kaydedilemedi:', err));
  };

  return (
    <form onSubmit={handleSubmit} className="add-form">
      <input name="platform" value={formData.platform} onChange={handleChange} placeholder="Platform" required />
      <input name="username" value={formData.username} onChange={handleChange} placeholder="Kullanıcı Adı" required />
      <input name="password" value={formData.password} onChange={handleChange} placeholder="Şifre" required />
      <textarea name="notes" value={formData.notes} onChange={handleChange} placeholder="Notlar" />

      <label>
        <input type="checkbox" name="isShared" checked={formData.isShared} onChange={handleChange} />
        Paylaş
      </label>

      {formData.isShared && (
        <select name="sharedWithUserId" value={formData.sharedWithUserId} onChange={handleChange}>
          <option value="">Kullanıcı Seç</option>
          {users.map(user => (
            <option key={user.id} value={user.id}>{user.fullName}</option>
          ))}
        </select>
      )}

      <button type="submit">{editingAccount ? "Güncelle" : "Ekle"}</button>
    </form>
  );
}

export default AddAccountForm;
