import React, { useEffect, useState } from "react";
import axios from "axios";
import AccountCard from "./AccountCard";
import AddAccountForm from "./addAccount";
import "../styles/MainLayout.css";

function Home() {
  const [accounts, setAccounts] = useState([]);
  const [activeTab, setActiveTab] = useState("personel");
  const [editingAccount, setEditingAccount] = useState(null);

  const fetchAccounts = () => {
    axios.get("http://localhost:5059/api/Accounts")
      .then(res => setAccounts(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = id => {
    axios.delete(`http://localhost:5059/api/Accounts/${id}`)
      .then(fetchAccounts)
      .catch(err => console.error(err));
  };

  const handleEdit = (account) => {
    setEditingAccount(account);
  };

  const handleFormSubmit = () => {
    setEditingAccount(null);
    fetchAccounts();
  };

  // Sadece paylaşılan hesapları filtrele
  const sharedAccounts = accounts.filter(acc => acc.isShared);

  return (
    <div className="home-container">
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active-tab" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilanlar" ? "active-tab" : ""}
          onClick={() => setActiveTab("paylasilanlar")}
        >
          Paylaşılanlar
        </button>
      </div>

      {activeTab === "personel" && (
        <>
          <h2>Personel Hesaplar</h2>
          <AddAccountForm
            onAccountAdded={handleFormSubmit}
            editingAccount={editingAccount}
          />
          <div className="account-list">
            {accounts.map((acc) => (
              <AccountCard
                key={acc.id}
                account={acc}
                onDelete={handleDelete}
                onEdit={handleEdit}
              />
            ))}
          </div>
        </>
      )}


    </div>
  );
}

export default Home;
