import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/MainLayout.css";
// CSS dosyasını birazdan vereceğim

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("https://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (error) {
      console.error("Veri alınamadı:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Bu hesabı silmek istediğinize emin misiniz?")) {
      try {
        await axios.delete(`https://localhost:5059/api/accounts/${id}`);
        fetchAccounts();
      } catch (error) {
        console.error("Silme hatası:", error);
      }
    }
  };

  const handleShare = async (id) => {
    try {
      await axios.put(`https://localhost:5059/api/Accounts/${id}/share`);
      fetchAccounts();
    } catch (error) {
      console.error("Paylaşma hatası:", error);
    }
  };

  const handleEdit = (account) => {
    // Güncelleme formuna yönlendirme yapılabilir veya modal açılabilir.
    console.log("Güncellenecek hesap:", account);
  };

  const filteredAccounts =
    activeTab === "personel"
      ? accounts.filter((a) => !a.isShared)
      : accounts.filter((a) => a.isShared);

  return (
    <div className="home-container">
      <div className="tab-bar">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      {loading ? (
        <p>Yükleniyor...</p>
      ) : (
        <div className="account-list">
          {filteredAccounts.length === 0 ? (
            <p>Hesap bulunamadı.</p>
          ) : (
            filteredAccounts.map((acc) => (
              <div key={acc.id} className="account-card">
                <h3>{acc.platform}</h3>
                <p><strong>Kullanıcı:</strong> {acc.username}</p>
                <p><strong>Email:</strong> {acc.email}</p>
                <p><strong>Notlar:</strong> {acc.notes}</p>
                <p><strong>Kategori:</strong> {acc.category}</p>
                <div className="card-buttons">
                  <button onClick={() => handleEdit(acc)}>Güncelle</button>
                  <button onClick={() => handleDelete(acc.id)}>Sil</button>
                  {activeTab === "personel" && (
                    <button onClick={() => handleShare(acc.id)}>Paylaş</button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default Home;
