import React, { useState } from "react";
import AccountList from "./AccountList";
import SharedAccountList from "./SharedAccountList";
import AddAccountForm from "./AddAccountForm";
import Settings from "./Settings";
import "../styles/MainLayout.css";

const Home = () => {
  const [activeTab, setActiveTab] = useState("personel");

  const renderTabContent = () => {
    switch (activeTab) {
      case "personel":
        return <AccountList />;
      case "paylasilanlar":
        return <SharedAccountList />;
      case "yonetim":
        return <AddAccountForm />;
      case "ayarlar":
        return <Settings />;
      default:
        return null;
    }
  };

  return (
    <div className="main-layout">
      <div className="sidebar">
        <button onClick={() => setActiveTab("personel")}>Personel</button>
        <button onClick={() => setActiveTab("paylasilanlar")}>Paylaşılanlar</button>
        <button onClick={() => setActiveTab("yonetim")}>Hesap Bilgileri Yönetim Sayfası</button>
        <button onClick={() => setActiveTab("ayarlar")}>Ayarlar Sayfası</button>
      </div>
      <div className="content">{renderTabContent()}</div>
    </div>
  );
};

export default Home;
