import React, { useState } from "react";
import Navbar from "./Navbar";
import Sidebar from "./Sidebar";
import AccountTable from "./AccountTable";
import AddAccountForm from "./AddAccountForm";
import "../styles/Home.css";

const Home = () => {
  const [activeTab, setActiveTab] = useState("personel");
  const [showForm, setShowForm] = useState(false);
  const [editingAccount, setEditingAccount] = useState(null);

  const handleAddClick = () => {
    setEditingAccount(null);
    setShowForm(true);
  };

  const handleEditClick = (account) => {
    setEditingAccount(account);
    setShowForm(true);
  };

  const handleFormClose = () => {
    setShowForm(false);
    setEditingAccount(null);
  };

  return (
    <div className="home-layout">
      <Sidebar />
      <div className="main-content">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        {activeTab === "personel" && (
          <>
            <button onClick={handleAddClick} className="add-button">+ Yeni Hesap</button>
            {showForm && (
              <AddAccountForm
                editingAccount={editingAccount}
                onAccountAdded={handleFormClose}
              />
            )}
            <AccountTable onEdit={handleEditClick} />
          </>
        )}
        {activeTab === "paylasilanlar" && <div>Paylaşılanlar gelecektir...</div>}
      </div>
    </div>
  );
};

export default Home;
