import React, { useState, useEffect } from "react";
import AddAccountForm from "./addAccount";
import EditAccountModal from "./EditAccountModal";
import axios from "axios";
import "../styles/Home.css";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [editingAccount, setEditingAccount] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [isEditModalOpen, setEditModalOpen] = useState(false);

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (error) {
      console.error("Veri √ßekme hatasƒ±:", error);
    }
  };

  const handleAddClick = () => {
    setEditingAccount(null);
    setShowAddForm(true);
  };

  const handleEditClick = (account) => {
    setEditingAccount(account);
    setEditModalOpen(true);
  };

  const handleDeleteClick = async (id) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${id}`);
      fetchAccounts();
    } catch (error) {
      console.error("Silme hatasƒ±:", error);
    }
  };

  const handleAccountSaved = () => {
    setShowAddForm(false);
    fetchAccounts();
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingAccount((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdateSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5059/api/accounts/${editingAccount.id}`, editingAccount);
      setEditModalOpen(false);
      fetchAccounts();
    } catch (error) {
      console.error("G√ºncelleme hatasƒ±:", error);
    }
  };

  return (
    <div className="home-container">
      <div className="sidebar">
        <div className="sidebar-item">‚öôÔ∏è Ayarlar</div>
        <div className="sidebar-item">üìÅ Hesap Y√∂netimi</div>
      </div>

      <div className="main-content">
        <div className="tabs">
          <button
            className={activeTab === "personel" ? "active" : ""}
            onClick={() => setActiveTab("personel")}
          >
            Personel
          </button>
          <button
            className={activeTab === "paylasilanlar" ? "active" : ""}
            onClick={() => setActiveTab("paylasilanlar")}
          >
            Payla≈üƒ±lanlar
          </button>
        </div>

        {activeTab === "personel" && (
          <div>
            <h2>Personel Hesaplarƒ±</h2>
            <button onClick={handleAddClick}>‚ûï Yeni Hesap Ekle</button>

            {showAddForm && (
              <AccountTable
                onAccountAdded={handleAccountSaved}
                editingAccount={editingAccount}
              />
            )}

            <table className="account-table">
              <thead>
                <tr>
                  <th>Platform</th>
                  <th>Kullanƒ±cƒ± Adƒ±</th>
                  <th>Payla≈üƒ±m</th>
                  <th>Not</th>
                  <th>ƒ∞≈ülemler</th>
                </tr>
              </thead>
              <tbody>
                {accounts.map((account) => (
                  <tr key={account.id}>
                    <td>{account.platform || "‚Äî"}</td>
                    <td>{account.username || "‚Äî"}</td>
                    <td>{account.isShared ? "Evet" : "Hayƒ±r"}</td>
                    <td>{account.notes || "‚Äî"}</td>
                    <td>
                      <button onClick={() => handleEditClick(account)}>‚úèÔ∏è</button>
                      <button onClick={() => handleDeleteClick(account.id)}>üóëÔ∏è</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === "paylasilanlar" && (
          <div>
            <h2>Payla≈üƒ±lan Hesaplar</h2>
            <table className="account-table">
              <thead>
                <tr>
                  <th>Platform</th>
                  <th>Kullanƒ±cƒ± Adƒ±</th>
                  <th>Not</th>
                  <th>Payla≈üan</th>
                </tr>
              </thead>
              <tbody>
                {accounts
                  .filter((a) => a.isShared)
                  .map((a) => (
                    <tr key={a.id}>
                      <td>{a.platform || "‚Äî"}</td>
                      <td>{a.username || "‚Äî"}</td>
                      <td>{a.notes || "‚Äî"}</td>
                      <td>{a.owner || "Bilinmiyor"}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <EditAccountModal
        isOpen={isEditModalOpen}
        onClose={() => setEditModalOpen(false)}
        account={editingAccount}
        onSave={handleUpdateSubmit}
        onChange={handleEditChange}
      />
    </div>
  );
}

export default Home;
