import React, { useEffect, useState } from "react";
import axios from "axios";
import AccountCard from "./AccountCard";
import AddAccountForm from "./addAccount";
import "../styles/MainLayout.css";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [editingAccount, setEditingAccount] = useState(null);

  const fetchAccounts = () => {
    axios.get("http://localhost:5059/api/Accounts")
      .then(res => setAccounts(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5059/api/Accounts/${id}`)
      .then(fetchAccounts)
      .catch(err => console.error(err));
  };

  const handleEdit = (account) => {
    setEditingAccount(account);
  };

  const handleFormSubmit = () => {
    setEditingAccount(null);
    fetchAccounts();
  };

  // Sekme değiştirici
  const renderTabs = () => (
    <div className="tab-container">
      <button
        className={activeTab === "personel" ? "tab active" : "tab"}
        onClick={() => setActiveTab("personel")}
      >
        Personel
      </button>
      <button
        className={activeTab === "paylasilanlar" ? "tab active" : "tab"}
        onClick={() => setActiveTab("paylasilanlar")}
      >
        Paylaşılanlar
      </button>
    </div>
  );

  // Şartlı filtreleme (şu anlık örnek olarak tüm liste geliyor)
  const filteredAccounts = activeTab === "personel"
    ? accounts.filter(acc => !acc.sharedWithOthers) // sadece personel
    : accounts.filter(acc => acc.sharedWithOthers); // sadece paylaşılan

  return (
    <div className="main-container">
      <h2>Hesaplar</h2>
      {renderTabs()}
      <AddAccountForm
        onAccountAdded={handleFormSubmit}
        editingAccount={editingAccount}
      />
      <div className="account-list">
        {filteredAccounts.map((acc) => (
          <AccountCard
            key={acc.id}
            account={acc}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
