import React, { useState } from "react";
import PersonelTab from "./PersonelTab";
import PaylasilanlarTab from "./PaylasilanlarTab";
import "./Home.css";import { Link } from "react-router-dom";

<div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
  <Link to="/account">Hesap Bilgileri</Link>
  <Link to="/settings">Ayarlar</Link>
</div>

export default function Home() {
  const [activeTab, setActiveTab] = useState("personel");

  return (
    <div style={{padding:20}}>
      <div className="home-tabs">
        <div 
          className={`home-tab ${activeTab === "personel" ? "active" : ""}`} 
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </div>
        <div 
          className={`home-tab ${activeTab === "paylasilanlar" ? "active" : ""}`} 
          onClick={() => setActiveTab("paylasilanlar")}
        >
          Paylaşılanlar
        </div>
      </div>

      {activeTab === "personel" && <PersonelTab />}
      {activeTab === "paylasilanlar" && <PaylasilanlarTab />}
    </div>
  );
}
