import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Home.css";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (err) {
      console.error("Hesaplar alınamadı:", err);
      setError("Hesaplar alınamadı");
    }
  };

  return (
    <div className="home-container">
      {/* Sol Panel */}
      <div className="sidebar">
        <button className="sidebar-btn">Ayarlar</button>
        <button className="sidebar-btn">Hesap Yönetimi</button>
      </div>

      {/* Ana İçerik */}
      <div className="main-content">
        {/* Sekmeler */}
        <div className="tabs">
          <button
            className={`tab ${activeTab === "personel" ? "active" : ""}`}
            onClick={() => setActiveTab("personel")}
          >
            Personel
          </button>
          <button
            className={`tab ${activeTab === "paylasilanlar" ? "active" : ""}`}
            onClick={() => setActiveTab("paylasilanlar")}
          >
            Paylaşılanlar
          </button>
        </div>

        {/* İçerik */}
        <div className="tab-content">
          {activeTab === "personel" && (
            <div>
              <h2>Personel Hesapları</h2>
              <div className="button-group">
                <button>Ekle</button>
                <button>Sil</button>
                <button>Güncelle</button>
                <button>Paylaş</button>
              </div>
              <ul>
                {accounts.map((acc) => (
                  <li key={acc.id}>
                    <strong>{acc.username}</strong> – {acc.email}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {activeTab === "paylasilanlar" && (
            <div>
              <h2>Paylaşılan Hesaplar</h2>
              <table className="account-table">
                <thead>
                  <tr>
                    <th>Kullanıcı Adı</th>
                    <th>Email</th>
                    <th>IP</th>
                    <th>Paylaşım Tarihi</th>
                  </tr>
                </thead>
                <tbody>
                  {accounts.map((acc) => (
                    <tr key={acc.id}>
                      <td>{acc.username}</td>
                      <td>{acc.email}</td>
                      <td>{acc.ipAddress}</td>
                      <td>{acc.sharedUntil || "Yok"}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
