import "../styles/MainLayout.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [editingAccount, setEditingAccount] = useState(null);
  const [editForm, setEditForm] = useState({ username: "", email: "", notes: "" });

  const fetchAccounts = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
      setLoading(false);
    } catch (err) {
      setError("Veriler alınırken hata oluştu.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = async (accountId) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${accountId}`);
      fetchAccounts();
    } catch (err) {
      alert("Silme işlemi başarısız.");
    }
  };

  const openEditModal = (account) => {
    setEditingAccount(account);
    setEditForm({
      username: account.username,
      email: account.email,
      notes: account.notes,
    });
  };

  const closeEditModal = () => {
    setEditingAccount(null);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditSave = async () => {
    if (!editForm.username || !editForm.email) {
      alert("Kullanıcı adı ve e-posta boş olamaz!");
      return;
    }

    try {
      await axios.put(`http://localhost:5059/api/accounts/${editingAccount.id}`, {
        ...editingAccount,
        username: editForm.username,
        email: editForm.email,
        notes: editForm.notes,
      });

      fetchAccounts();
      closeEditModal();
    } catch (error) {
      alert("Güncelleme başarısız.");
    }
  };

  const renderAccounts = () => {
    if (loading) return <p>Yükleniyor...</p>;
    if (error) return <p>{error}</p>;

    return (
      <ul className="account-list">
        {accounts.map((account) => (
          <li key={account.id} className="account-item">
            <strong>Kullanıcı Adı:</strong> {account.username} <br />
            <strong>E-posta:</strong> {account.email} <br />
            <strong>Not:</strong> {account.notes} <br />
            <button onClick={() => openEditModal(account)}>Güncelle</button>
            <button onClick={() => handleDelete(account.id)}>Sil</button>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="main-content">
      <h2>NDAccountManager - Ana Sayfa</h2>
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      <div className="tab-content">
        {activeTab === "personel" && renderAccounts()}
        {activeTab === "paylasilan" && <p>Paylaşılan hesaplar burada görünecek.</p>}
      </div>

      {editingAccount && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Hesabı Güncelle</h3>
            <label>
              Kullanıcı Adı:
              <input
                type="text"
                name="username"
                value={editForm.username}
                onChange={handleEditChange}
              />
            </label>
            <label>
              E-posta:
              <input
                type="email"
                name="email"
                value={editForm.email}
                onChange={handleEditChange}
              />
            </label>
            <label>
              Not:
              <textarea
                name="notes"
                value={editForm.notes}
                onChange={handleEditChange}
              ></textarea>
            </label>
            <div className="modal-buttons">
              <button onClick={handleEditSave}>Kaydet</button>
              <button onClick={closeEditModal}>İptal</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
