import { getUserRole } from "./roleUtils"; // yukarıdaki getUserRole fonksiyonun

export function getUserRoleFromAccount(account) {
  const groups =
    account?.idTokenClaims?.groups || account?.idTokenClaims?._claim_names?.groups;

  if (!groups) {
    console.warn("Kullanıcı grubu bulunamadı.");
    return "Unknown";
  }

  // Eğer sadece _claim_names varsa (kullanıcı çok fazla gruptaysa), uyarı ver
  if (account?.idTokenClaims?._claim_names?.groups) {
    console.warn(
      "Çok fazla grup var, bu nedenle gruplar token içinde gelmedi. Instead, you must call Microsoft Graph API to fetch group memberships."
    );
    return "Unknown";
  }

  return getUserRole(groups);
}
