import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Home.css";

import { useUserGroups } from "../hooks/useUserGroups";
import { getUserRole } from "../utils/groupUtils";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedAccountId, setSelectedAccountId] = useState(null);
  const [updatedAccount, setUpdatedAccount] = useState({
    title: "",
    username: "",
    password: "",
    notes: "",
  });

  const [isAddMode, setIsAddMode] = useState(false); // 🔹 Ekleme modunu ayırmak için

  const { getUserGroups } = useUserGroups();
  const [userRole, setUserRole] = useState("Yükleniyor...");

  useEffect(() => {
    async function fetchRole() {
      const groups = await getUserGroups();
      const role = getUserRole(groups);
      setUserRole(role);
    }
    fetchRole();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (error) {
      console.error("Veriler alınamadı:", error);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${id}`);
      fetchAccounts();
    } catch (error) {
      console.error("Silme başarısız:", error);
    }
  };

  const handleUpdateClick = (account) => {
    setSelectedAccountId(account.id);
    setUpdatedAccount({
      title: account.title,
      username: account.username,
      password: account.password,
      notes: account.notes,
    });
    setIsAddMode(false); // Güncelleme moduna geç
    setShowModal(true);
  };

  const handleUpdateSave = async () => {
    try {
      await axios.put(`http://localhost:5059/api/accounts/${selectedAccountId}`, updatedAccount);
      setShowModal(false);
      fetchAccounts();
    } catch (error) {
      console.error("Güncelleme başarısız:", error);
    }
  };

  const handleAddClick = () => {
    setIsAddMode(true);
    setUpdatedAccount({ title: "", username: "", password: "", notes: "" });
    setShowModal(true);
  };

  const handleAddAccount = async () => {
    try {
      await axios.post(`http://localhost:5059/api/accounts`, updatedAccount);
      setShowModal(false);
      fetchAccounts();
    } catch (error) {
      console.error("Ekleme başarısız:", error);
    }
  };

  return (
    <div className="home-container">
      <div className="tab-header">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      <div className="role-info">
        <p><strong>Rol:</strong> {userRole}</p>
      </div>

      {userRole === "Manager" && (
        <div style={{ marginBottom: "1rem" }}>
          <button onClick={handleAddClick} className="btn-add">➕ Yeni Hesap Ekle</button>
        </div>
      )}

      <div className="account-list">
        {accounts.map((account) => (
          <div key={account.id} className="account-card">
            <h3>{account.title}</h3>
            <p><strong>Kullanıcı Adı:</strong> {account.username}</p>
            <p><strong>Şifre:</strong> {account.password}</p>
            <p><strong>Not:</strong> {account.notes}</p>

            {userRole === "Manager" && (
              <div className="button-group">
                <button onClick={() => handleUpdateClick(account)}>✏️ Güncelle</button>
                <button onClick={() => handleDelete(account.id)}>🗑️ Sil</button>
              </div>
            )}
          </div>
        ))}
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{isAddMode ? "➕ Yeni Hesap Ekle" : "🔧 Hesap Bilgilerini Güncelle"}</h2>

            <div className="input-group">
              <label>Başlık</label>
              <input
                type="text"
                placeholder="Hesap başlığı girin"
                value={updatedAccount.title}
                onChange={(e) => setUpdatedAccount({ ...updatedAccount, title: e.target.value })}
              />
            </div>

            <div className="input-group">
              <label>Kullanıcı Adı</label>
              <input
                type="text"
                placeholder="Kullanıcı adı"
                value={updatedAccount.username}
                onChange={(e) => setUpdatedAccount({ ...updatedAccount, username: e.target.value })}
              />
            </div>

            <div className="input-group">
              <label>Şifre</label>
              <input
                type="password"
                placeholder="Şifre"
                value={updatedAccount.password}
                onChange={(e) => setUpdatedAccount({ ...updatedAccount, password: e.target.value })}
              />
            </div>

            <div className="input-group">
              <label>Notlar</label>
              <textarea
                placeholder="Notlar"
                rows="3"
                value={updatedAccount.notes}
                onChange={(e) => setUpdatedAccount({ ...updatedAccount, notes: e.target.value })}
              ></textarea>
            </div>

            <div className="modal-buttons">
              <button className="save-btn" onClick={isAddMode ? handleAddAccount : handleUpdateSave}>
                💾 Kaydet
              </button>
              <button className="cancel-btn" onClick={() => setShowModal(false)}>❌ İptal</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
