import React, { useState, useEffect } from "react";
import axios from "axios";
 // CSS dosyası aşağıda

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editAccount, setEditAccount] = useState(null);

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/Accounts");
      setAccounts(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Veriler alınamadı:", error);
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Bu hesabı silmek istediğinize emin misiniz?")) {
      try {
        await axios.delete(`http://localhost:5059/api/Accounts/${id}`);
        fetchAccounts(); // Yeniden yükle
      } catch (error) {
        alert("Silme işlemi başarısız.");
      }
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditAccount({ ...editAccount, [name]: value });
  };

  const handleEditSave = async () => {
    try {
      await axios.put(`http://localhost:5059/api/Accounts/${editAccount.id}`, editAccount);
      setEditAccount(null);
      fetchAccounts();
    } catch (error) {
      alert("Güncelleme başarısız.");
    }
  };

  const renderAccountCard = (account) => (
    <div className="account-card" key={account.id}>
      {editAccount?.id === account.id ? (
        <>
          <input name="username" value={editAccount.username} onChange={handleEditChange} />
          <input name="email" value={editAccount.email} onChange={handleEditChange} />
          <input name="password" value={editAccount.password} onChange={handleEditChange} />
          <textarea name="notes" value={editAccount.notes} onChange={handleEditChange} />
          <button onClick={handleEditSave}>Kaydet</button>
          <button onClick={() => setEditAccount(null)}>İptal</button>
        </>
      ) : (
        <>
          <h4>{account.username}</h4>
          <p>Email: {account.email}</p>
          <p>Not: {account.notes}</p>
          {activeTab === "personel" && (
            <div className="button-group">
              <button onClick={() => setEditAccount(account)}>Güncelle</button>
              <button onClick={() => handleDelete(account.id)}>Sil</button>
            </div>
          )}
        </>
      )}
    </div>
  );

  return (
    <div className="home-container">
      <div className="tab-header">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      {loading ? (
        <p>Yükleniyor...</p>
      ) : (
        <div className="account-list">
          {accounts
            .filter((acc) =>
              activeTab === "personel"
                ? !acc.isShared
                : acc.isShared // burada acc.isShared değeri backend'den gelmeli
            )
            .map(renderAccountCard)}
        </div>
      )}
    </div>
  );
}

export default Home;
