export const msalConfig = {
  auth: {
    clientId: "552a6af7-31b4-4de6-a7d7-cf20278bde03", // senin client ID
    authority: "https://login.microsoftonline.com/3cf27b57-334d-4413-aebc-ab5a0a99f493", // senin tenant ID
    redirectUri: "http://localhost:3000", // Azure'dakiyle aynı olmalı
  },
  cache: {
    cacheLocation: "localStorage",
    storeAuthStateInCookie: false,
  },
};

// GİRİŞ İSTEĞİ KONFİGÜRASYONU EKLENİYOR ✅
export const loginRequest = {
  scopes: ["User.Read"], // Basit giriş için yeterli
};
