import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Home from "./components/Home";
import AccountDetails from "./components/AccountDetails";
import Settings from "./components/Settings";

function App() {
  const [userRole, setUserRole] = useState(null); // "Manager" veya "User"

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login setUserRole={setUserRole} />} />
        <Route path="/home" element={<Home userRole={userRole} />} />
        <Route path="/account" element={<AccountDetails userRole={userRole} />} />
        <Route
          path="/settings"
          element={
            userRole === "Manager" ? <Settings /> : <Navigate to="/home" replace />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
