import React, { useState, useEffect } from "react";
import axios from "axios";
import AddAccount from "./addAccount";
import AccountList from "./AccountList";
import "../styles/MainLayout.css";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
      setLoading(false);
    } catch (err) {
      setError("Veriler alınırken hata oluştu.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const filteredAccounts = accounts.filter((account) =>
    activeTab === "personel" ? !account.isShared : account.isShared
  );

  return (
    <div className="home-container">
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => handleTabChange("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => handleTabChange("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      {loading ? (
        <p>Yükleniyor...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <AccountList accounts={filteredAccounts} />
      )}

      <AddAccount onAccountAdded={fetchAccounts} />
    </div>
  );
}

export default Home;
