import React, { useState, useEffect } from "react";
import "../styles/Add.css";
function AddAccountForm({ onAccountAdded, editingAccount }) {
  const [platform, setPlatform] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [notes, setNotes] = useState("");
  const [isShared, setIsShared] = useState(false);

  useEffect(() => {
    if (editingAccount) {
      setPlatform(editingAccount.platform);
      setUsername(editingAccount.username);
      setPassword(editingAccount.password);
      setNotes(editingAccount.notes);
      setIsShared(editingAccount.isShared);
    }
  }, [editingAccount]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Burada ekleme veya güncelleme işlemi yapılmalı
    // Örneğin axios ile POST veya PUT isteği
    // Sonra onAccountAdded çağrılmalı
    onAccountAdded();
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <input placeholder="Platform" value={platform} onChange={e => setPlatform(e.target.value)} required />
      <input placeholder="Kullanıcı Adı" value={username} onChange={e => setUsername(e.target.value)} required />
      <input placeholder="Şifre" type="password" value={password} onChange={e => setPassword(e.target.value)} required />
      <textarea placeholder="Notlar" value={notes} onChange={e => setNotes(e.target.value)} />
      <label>
        <input type="checkbox" checked={isShared} onChange={e => setIsShared(e.target.checked)} />
        Paylaşılsın mı?
      </label>
      <button type="submit">Kaydet</button>
    </form>
  );
}

export default AddAccountForm;
