import React, { useState } from "react";
import axios from "axios";

const AddAccount = () => {
  const [account, setAccount] = useState({
    z
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setAccount((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5059/api/Accounts", account);
      console.log("Hesap başarıyla eklendi:", response.data);
    } catch (error) {
      console.error("Hesap kaydedilemedi:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="platform" placeholder="Platform" onChange={handleChange} />
      <input name="username" placeholder="Kullanıcı Adı" onChange={handleChange} />
      <input name="email" placeholder="E-posta" onChange={handleChange} />
      <input name="password" placeholder="Şifre" onChange={handleChange} />
      <textarea name="notes" placeholder="Notlar" onChange={handleChange} />
      <input name="ipAddress" placeholder="IP Adresi" onChange={handleChange} />
      <input name="category" placeholder="Kategori" onChange={handleChange} />

      <label>
        Paylaşılsın mı?
        <input
          type="checkbox"
          name="isShared"
          checked={account.isShared}
          onChange={handleChange}
        />
      </label>

      <button type="submit">Hesap Ekle</button>
    </form>
  );
};

export default AddAccount;
