import React, { useState } from "react";
import axios from "axios";
import "../styles/MainLayout.css";

const AddAccount = ({ onAccountAdded }) => {
  const [formData, setFormData] = useState({
    platform: "",
    username: "",
    password: "",
    notes: "",
    ipAddress: "",
    category: "",
    isShared: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5059/api/Accounts", formData);
      alert("Hesap başarıyla eklendi!");
      setFormData({
        platform: "",
        username: "",
        password: "",
        notes: "",
        ipAddress: "",
        category: "",
        isShared: false,
      });
      onAccountAdded(); // Listeyi güncellemek için callback
    } catch (error) {
      console.error("Hesap eklenemedi:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="account-form">
      <input type="text" name="platform" placeholder="Platform" value={formData.platform} onChange={handleChange} required />
      <input type="text" name="username" placeholder="Kullanıcı Adı" value={formData.username} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Şifre" value={formData.password} onChange={handleChange} required />
      <input type="text" name="notes" placeholder="Notlar" value={formData.notes} onChange={handleChange} />
      <input type="text" name="ipAddress" placeholder="IP Adresi" value={formData.ipAddress} onChange={handleChange} />
      <input type="text" name="category" placeholder="Kategori" value={formData.category} onChange={handleChange} />
      <label>
        <input type="checkbox" name="isShared" checked={formData.isShared} onChange={handleChange} />
        Paylaşılsın mı?
      </label>
      <button type="submit">Ekle</button>
    </form>
  );
};

export default AddAccount;
