import React, { useState } from "react";
import axios from "axios";
import "../styles/AddAccountForm.css"; // veya form bileşeni içinden gelen class’lara göre

const AddAccountForm = ({ onAccountAdded }) => {
  const [account, setAccount] = useState({
    platform: "",
    username: "",
    email: "",
    password: "",
    notes: "",
    isShared: false,
    ipAddress: "",
    category: ""
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setAccount({
      ...account,
      [name]: type === "checkbox" ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5059/api/Accounts", account);
      console.log("Hesap başarıyla eklendi:", response.data);
      if (onAccountAdded) onAccountAdded(); // Listeyi güncelle
    } catch (error) {
      console.error("Hesap kaydedilemedi:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="platform" value={account.platform} onChange={handleChange} placeholder="Platform" />
      <input name="username" value={account.username} onChange={handleChange} placeholder="Kullanıcı Adı" />
      <input name="email" value={account.email} onChange={handleChange} placeholder="E-posta" />
      <input name="password" value={account.password} onChange={handleChange} placeholder="Şifre" />
      <textarea name="notes" value={account.notes} onChange={handleChange} placeholder="Notlar" />
      <input name="ipAddress" value={account.ipAddress} onChange={handleChange} placeholder="IP Adresi" />
      <input name="category" value={account.category} onChange={handleChange} placeholder="Kategori" />
      <label>
        Paylaşılan mı?
        <input type="checkbox" name="isShared" checked={account.isShared} onChange={handleChange} />
      </label>
      <button type="submit">Hesap Ekle</button>
    </form>
  );
};

export default AddAccountForm;
