import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/MainLayout.css";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const response = await axios.get(`http://localhost:500/api/accounts/${activeTab}`);
        setAccounts(response.data);
        setLoading(false);
      } catch (err) {
        setError("Veriler alınamadı.");
        setLoading(false);
      }
    };

    fetchAccounts();
  }, [activeTab]);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const handleEdit = (accountId) => {
    console.log("Düzenle:", accountId);
    // düzenleme formu vs.
  };

  const handleDelete = async (accountId) => {
    try {
      await axios.delete(`http://localhost:5008/api/accounts/${accountId}`);
      setAccounts((prev) => prev.filter((a) => a.id !== accountId));
    } catch (err) {
      console.error("Silme işlemi başarısız:", err);
    }
  };

  const handleShare = (accountId) => {
    console.log("Paylaş:", accountId);
    // paylaşma işlemleri burada yapılır
  };

  return (
    <div className="container">
      <div className="tab-buttons">
        <button
          className={`tab-button ${activeTab === "personel" ? "active" : ""}`}
          onClick={() => handleTabClick("personel")}
        >
          Personel
        </button>
        <button
          className={`tab-button ${activeTab === "paylasilanlar" ? "active" : ""}`}
          onClick={() => handleTabClick("paylasilanlar")}
        >
          Paylaşılanlar
        </button>
      </div>

      {loading && <p>Yükleniyor...</p>}
      {error && <p>{error}</p>}

      {!loading && !error && (
        <div className="account-list">
          {accounts.map((account) => (
            <div key={account.id} className="account-card">
              <div className="account-title">{account.platform}</div>
              <div className="account-field">Kullanıcı Adı: {account.username}</div>
              <div className="account-field">Email: {account.email}</div>
              <div className="account-field">IP: {account.ipAddress}</div>
              <div className="account-field">Not: {account.note}</div>

              <div className="button-group">
                <button className="btn btn-edit" onClick={() => handleEdit(account.id)}>Düzenle</button>
                <button className="btn btn-delete" onClick={() => handleDelete(account.id)}>Sil</button>
                {userRole === "Manager" && (
                  <button className="btn btn-share" onClick={() => handleShare(account.id)}>Paylaş</button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
