import React, { useState } from "react";
import axios from "axios";
import "../styles/AddAccountForm.css"; // veya form bileşeni içinden gelen class’lara göre



function AddAccount({ onAccountAdded }) {
  const [form, setForm] = useState({
    platform: "",
    username: "",
    email: "",
    password: "",
    notes: "",
    isShared: false,
    ipAddress: "",
    category: "",
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === "checkbox" ? checked : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5059/api/Accounts", form);
      onAccountAdded(response.data);
      alert("Hesap başarıyla eklendi!");
      setForm({
        platform: "",
        username: "",
        email: "",
        password: "",
        notes: "",
        isShared: false,
        ipAddress: "",
        category: "",
      });
    } catch (error) {
      console.error("Hesap kaydedilemedi:", error);
    }
  };

  return (
    <form className="add-account-form" onSubmit={handleSubmit}>
      <input name="platform" placeholder="Platform" value={form.platform} onChange={handleChange} required />
      <input name="username" placeholder="Kullanıcı Adı" value={form.username} onChange={handleChange} />
      <input name="email" placeholder="E-posta" value={form.email} onChange={handleChange} />
      <input name="password" placeholder="Şifre" value={form.password} onChange={handleChange} />
      <input name="ipAddress" placeholder="IP Adresi" value={form.ipAddress} onChange={handleChange} />
      <input name="category" placeholder="Kategori" value={form.category} onChange={handleChange} />
      <textarea name="notes" placeholder="Notlar" value={form.notes} onChange={handleChange} />
      <label>
        <input type="checkbox" name="isShared" checked={form.isShared} onChange={handleChange} />
        Paylaşılan hesap mı?
      </label>
      <button type="submit">Hesap Ekle</button>
    </form>
  );
}

export default AddAccount;
