import React from "react";
import { useMsal } from "@azure/msal-react";
import { InteractionType } from "@azure/msal-browser";

const Login = () => {
  const { instance, accounts } = useMsal();

  const handleLogin = () => {
    instance.loginPopup({
      scopes: ["user.read"], // Temel profil bilgileri için
      prompt: "select_account"
    }).catch(e => {
      console.error(e);
    });
  };

  const handleLogout = () => {
    instance.logoutPopup();
  };

  return (
    <div style={styles.container}>
      {accounts.length > 0 ? (
        <>
          <h2 style={styles.text}>Hoş geldin, {accounts[0].username}</h2>
          <button style={styles.button} onClick={handleLogout}>
            Çıkış Yap
          </button>
        </>
      ) : (
        <>
          <h2 style={styles.text}>NDAccountManager Giriş</h2>
          <button style={styles.button} onClick={handleLogin}>
            Microsoft ile Giriş Yap
          </button>
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#fff8f0"
  },
  text: {
    marginBottom: "20px",
    color: "#ff7a00",
    fontSize: "24px"
  },
  button: {
    padding: "10px 20px",
    backgroundColor: "#ff7a00",
    border: "none",
    borderRadius: "8px",
    color: "#fff",
    fontSize: "16px",
    cursor: "pointer"
  }
};

export default Login;
