import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Home.css";// CSS'yi bu dosyada tutuyoruz

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedAccountId, setSelectedAccountId] = useState(null);
  const [updatedAccount, setUpdatedAccount] = useState({
    title: "",
    username: "",
    password: "",
    notes: "",
  });

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (error) {
      console.error("Veriler alƒ±namadƒ±:", error);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${id}`);
      fetchAccounts();
    } catch (error) {
      console.error("Silme ba≈üarƒ±sƒ±z:", error);
    }
  };

  const handleUpdateClick = (account) => {
    setSelectedAccountId(account.id);
    setUpdatedAccount({
      title: account.title,
      username: account.username,
      password: account.password,
      notes: account.notes,
    });
    setShowModal(true);
  };

  const handleUpdateSave = async () => {
    try {
      await axios.put(`http://localhost:5059/api/accounts/${selectedAccountId}`, updatedAccount);
      setShowModal(false);
      fetchAccounts();
    } catch (error) {
      console.error("G√ºncelleme ba≈üarƒ±sƒ±z:", error);
    }
  };

  return (
    <div className="home-container">
      <div className="tab-header">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Payla≈üƒ±lanlar
        </button>
      </div>

      <div className="account-list">
        {accounts.map((account) => (
          <div key={account.id} className="account-card">
            <h3>{account.title}</h3>
            <p><strong>Kullanƒ±cƒ± Adƒ±:</strong> {account.username}</p>
            <p><strong>≈ûifre:</strong> {account.password}</p>
            <p><strong>Not:</strong> {account.notes}</p>
            <div className="button-group">
              <button onClick={() => handleUpdateClick(account)}>G√ºncelle</button>
              <button onClick={() => handleDelete(account.id)}>Sil</button>
            </div>
          </div>
        ))}
      </div>

{showModal && (
  <div className="modal-overlay">
    <div className="modal">
      <h2>üîß Hesap Bilgilerini G√ºncelle</h2>

      <div className="input-group">
        <label>Ba≈ülƒ±k</label>
        <input
          type="text"
          placeholder="Hesap ba≈ülƒ±ƒüƒ± girin"
          value={updatedAccount.title}
          onChange={(e) =>
            setUpdatedAccount({ ...updatedAccount, title: e.target.value })
          }
        />
      </div>

      <div className="input-group">
        <label>Kullanƒ±cƒ± Adƒ±</label>
        <input
          type="text"
          placeholder="Kullanƒ±cƒ± adƒ±"
          value={updatedAccount.username}
          onChange={(e) =>
            setUpdatedAccount({ ...updatedAccount, username: e.target.value })
          }
        />
      </div>

      <div className="input-group">
        <label>≈ûifre</label>
        <input
          type="password"
          placeholder="≈ûifre"
          value={updatedAccount.password}
          onChange={(e) =>
            setUpdatedAccount({ ...updatedAccount, password: e.target.value })
          }
        />
      </div>

      <div className="input-group">
        <label>Notlar</label>
        <textarea
          placeholder="Notlar"
          rows="3"
          value={updatedAccount.notes}
          onChange={(e) =>
            setUpdatedAccount({ ...updatedAccount, notes: e.target.value })
          }
        ></textarea>
      </div>

      <div className="modal-buttons">
        <button className="save-btn" onClick={handleUpdateSave}>
          üíæ Kaydet
        </button>
        <button className="cancel-btn" onClick={() => setShowModal(false)}>
          ‚ùå ƒ∞ptal
        </button>
      </div>
    </div>
  </div>
)}
</div>
  );
}
export default Home;
