import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/AddAccountForm.css";

function AccountForm({ account, onSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    username: account?.username || "",
    password: account?.password || "",
    email: account?.email || "",
    ipAddress: account?.ipAddress || "",
    notes: account?.notes || "",
    sharedWithUserIds: account?.sharedWithUsers?.map(user => user.id) || [],
  });

  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5059/api/users")
      .then((res) => setUsers(res.data))
      .catch((err) => console.error("Kullanıcılar yüklenemedi:", err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "sharedWithUserIds") {
      const selectedOptions = Array.from(e.target.selectedOptions).map(option => option.value);
      setFormData({ ...formData, sharedWithUserIds: selectedOptions });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...formData,
      sharedWithUserIds: formData.sharedWithUserIds,
    };

    try {
      if (account?.id) {
        await axios.put(`http://localhost:5000/api/accounts/${account.id}`, payload);
      } else {
        await axios.post("http://localhost:5000/api/accounts", payload);
      }
      onSuccess();
    } catch (err) {
      console.error("Kaydetme hatası:", err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-semibold">{account ? "Hesabı Güncelle" : "Yeni Hesap Ekle"}</h2>

      <div>
        <label className="block">Kullanıcı Adı:</label>
        <input
          type="text"
          name="username"
          value={formData.username}
          onChange={handleChange}
          required
          className="w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block">Şifre:</label>
        <input
          type="text"
          name="password"
          value={formData.password}
          onChange={handleChange}
          className="w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block">Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block">IP Adresi:</label>
        <input
          type="text"
          name="ipAddress"
          value={formData.ipAddress}
          onChange={handleChange}
          className="w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block">Notlar:</label>
        <textarea
          name="notes"
          value={formData.notes}
          onChange={handleChange}
          className="w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block">Paylaşılacak Kullanıcılar:</label>
        <select
          name="sharedWithUserIds"
          value={formData.sharedWithUserIds}
          onChange={handleChange}
          multiple
          className="w-full border p-2 rounded h-32"
        >
          {users.map((user) => (
            <option key={user.id} value={user.id}>
              {user.displayName}
            </option>
          ))}
        </select>
      </div>

      <div className="flex gap-2 justify-end">
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          {account ? "Güncelle" : "Ekle"}
        </button>
        <button type="button" onClick={onCancel} className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500">
          İptal
        </button>
      </div>
    </form>
  );
}

export default AccountForm;
