import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/MainLayout.css";
const userGroups = ["Development", "Support", "Sale", "Manager"];

function AddAccountForm({ onAccountAdded }) {
  const [platform, setPlatform] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [notes, setNotes] = useState("");
  const [ipAddress, setIpAddress] = useState("");
  const [category, setCategory] = useState("");
  const [isShared, setIsShared] = useState(false);
  const [sharedWith, setSharedWith] = useState([]);

  const handleGroupChange = (e) => {
    const group = e.target.value;
    setSharedWith((prev) =>
      e.target.checked
        ? [...prev, group]
        : prev.filter((g) => g !== group)
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const account = {
        platform,
        username,
        email,
        password,
        notes,
        ipAddress,
        category,
        isShared,
        sharedWith,
      };

      const response = await axios.post("http://localhost:5059/api/Accounts", account);
      console.log("Hesap başarıyla eklendi:", response.data);
      onAccountAdded();
    } catch (error) {
      console.error("Hesap kaydedilemedi:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="add-account-form">
      <input value={platform} onChange={(e) => setPlatform(e.target.value)} placeholder="Platform" required />
      <input value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Kullanıcı Adı" required />
      <input value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
      <input value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Şifre" required />
      <textarea value={notes} onChange={(e) => setNotes(e.target.value)} placeholder="Notlar" />
      <input value={ipAddress} onChange={(e) => setIpAddress(e.target.value)} placeholder="IP Adresi" />
      <input value={category} onChange={(e) => setCategory(e.target.value)} placeholder="Kategori" />

      <label>
        <input
          type="checkbox"
          checked={isShared}
          onChange={(e) => setIsShared(e.target.checked)}
        />
        Paylaşılsın mı?
      </label>

      {isShared && (
        <div>
          <p>Kiminle paylaşılacak?</p>
          {userGroups.map((group) => (
            <label key={group}>
              <input
                type="checkbox"
                value={group}
                checked={sharedWith.includes(group)}
                onChange={handleGroupChange}
              />
              {group}
            </label>
          ))}
        </div>
      )}

      <button type="submit">Kaydet</button>
    </form>
  );
}

export default AddAccountForm;
