import React, { useEffect, useState } from "react";
import axios from "axios";
import AccountList from "./AccountList";

import "../styles/MainLayout.css";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/Accounts");
      setAccounts(response.data);
    } catch (error) {
      console.error("Veriler alınamadı:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleAccountAdded = (newAccount) => {
    setAccounts([...accounts, newAccount]);
  };

  const handleAccountDeleted = (id) => {
    setAccounts(accounts.filter((a) => a.id !== id));
  };

  const handleAccountUpdated = (updatedAccount) => {
    setAccounts(
      accounts.map((a) => (a.id === updatedAccount.id ? updatedAccount : a))
    );
  };

  return (
    <div className="main-container">
      <h1>Hesaplar</h1>
      <div className="tab-container">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilanlar" ? "active" : ""}
          onClick={() => setActiveTab("paylasilanlar")}
        >
          Paylaşılanlar
        </button>
      </div>

      {activeTab === "personel" && (
        <>
          <AddAccount onAccountAdded={handleAccountAdded} />
          <AccountList
            accounts={accounts.filter((a) => !a.isShared)}
            onDelete={handleAccountDeleted}
            onUpdate={handleAccountUpdated}
            canEdit={true}
          />
        </>
      )}

      {activeTab === "paylasilanlar" && (
        <AccountList
          accounts={accounts.filter((a) => a.isShared)}
          canEdit={false}
        />
      )}
    </div>
  );
}

export default Home;
