import React, { useEffect, useState } from "react";
import axios from "axios";

const AccountList = () => {
  const [accounts, setAccounts] = useState([]);

useEffect(() => {
    // API'den hesap verilerini çek
    axios
      .get("http://localhost:5059/api/Accounts")
      .then((response) => {
        setAccounts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Veri alınamadı:", error);
        setError("Veri alınamadı.");
        setLoading(false);
      });
  }, []);


  return (
    <div>
      <h2>Hesaplar</h2>
      <ul>
        {accounts.map((account, index) => (
          <li key={index}>
            <strong>{account.platform}</strong> - {account.username} <br />
            <em>{account.notes}</em>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AccountList;
