import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/MainLayout.css";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchAccounts = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
      setLoading(false);
    } catch (err) {
      setError("Veriler alınırken hata oluştu.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleDelete = async (accountId) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${accountId}`);
      fetchAccounts();
    } catch (err) {
      alert("Silme işlemi başarısız.");
    }
  };

  const handleEdit = async (account) => {
    const newUsername = prompt("Yeni kullanıcı adı:", account.username);
    const newEmail = prompt("Yeni e-posta:", account.email);
    const newNotes = prompt("Yeni not:", account.notes);

    if (!newUsername || !newEmail) {
      alert("Kullanıcı adı ve e-posta boş olamaz!");
      return;
    }

    try {
      await axios.put(`http://localhost:5059/api/accounts/${account.id}`, {
        ...account,
        username: newUsername,
        email: newEmail,
        notes: newNotes,
      });

      fetchAccounts();
    } catch (error) {
      console.error("Güncelleme hatası:", error);
      alert("Güncelleme başarısız.");
    }
  };

  const renderAccounts = () => {
    if (loading) return <p>Yükleniyor...</p>;
    if (error) return <p>{error}</p>;

    return (
      <ul className="account-list">
        {accounts.map((account) => (
          <li key={account.id} className="account-item">
            <strong>Kullanıcı Adı:</strong> {account.username} <br />
            <strong>E-posta:</strong> {account.email} <br />
            <strong>Not:</strong> {account.notes} <br />
            <button onClick={() => handleEdit(account)}>Güncelle</button>
            <button onClick={() => handleDelete(account.id)}>Sil</button>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="main-content">
      <h2>NDAccountManager - Ana Sayfa</h2>
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilan" ? "active" : ""}
          onClick={() => setActiveTab("paylasilan")}
        >
          Paylaşılanlar
        </button>
      </div>

      <div className="tab-content">
        {activeTab === "personel" && renderAccounts()}
        {activeTab === "paylasilan" && <p>Paylaşılan hesaplar burada görünecek.</p>}
      </div>
    </div>
  );
}

export default Home;
