import React, { useState, useEffect } from "react";
import axios from "axios";
import AddAccountForm from "./AddAccountForm";
import "../styles/MainLayout.css";

function Home({ userRole }) {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API'den verileri çek
  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (err) {
      setError("Veriler alınırken hata oluştu.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  // Yeni hesap eklendiğinde listeyi güncelle
  const handleAccountAdded = () => {
    fetchAccounts();
  };

  const renderAccounts = () => {
    if (loading) return <p>Yükleniyor...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;

    const filtered = accounts.filter((acc) =>
      activeTab === "personel" ? !acc.isShared : acc.isShared
    );

    if (filtered.length === 0) return <p>Hiç hesap kaydı yok.</p>;

    return (
      <ul className="account-list">
        {filtered.map((acc) => (
          <li key={acc.id} className="account-card">
            <h4>{acc.platform}</h4>
            <p><strong>Kullanıcı Adı:</strong> {acc.username}</p>
            <p><strong>IP:</strong> {acc.ipAddress || "-"}</p>
            <p><strong>Notlar:</strong> {acc.notes || "-"}</p>
            <p><strong>Paylaşılan:</strong> {acc.isShared ? "Evet" : "Hayır"}</p>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="home-container">
      <div className="tabs">
        <button
          className={activeTab === "personel" ? "active" : ""}
          onClick={() => setActiveTab("personel")}
        >
          Personel
        </button>
        <button
          className={activeTab === "paylasilanlar" ? "active" : ""}
          onClick={() => setActiveTab("paylasilanlar")}
        >
          Paylaşılanlar
        </button>
      </div>

      <div className="tab-content">
        <h3>{activeTab === "personel" ? "Kaydedilen Hesaplar" : "Paylaşılan Hesaplar"}</h3>
        {renderAccounts()}
      </div>

      <div className="add-account-form">
        <h3>Yeni Hesap Ekle</h3>
        <AddAccountForm onAccountAdded={handleAccountAdded} />
      </div>
    </div>
  );
}

export default Home;
