import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Home.css";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [error, setError] = useState(null);

  // Sayfa y√ºklendiƒüinde verileri √ßek
  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = async () => {
    try {
      const response = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(response.data);
    } catch (err) {
      console.error("Hesaplar alƒ±namadƒ±:", err);
      setError("Veriler alƒ±namadƒ±.");
    }
  };

  return (
    <div className="home-container">
      {/* Sol panel */}
      <div className="left-panel">
        <div className="side-option">‚öôÔ∏è Ayarlar</div>
        <div className="side-option">üîê Hesap Y√∂netimi</div>
      </div>

      {/* Ana i√ßerik */}
      <div className="main-content">
        <div className="tabs">
          <button
            className={`tab-button ${activeTab === "personel" ? "active" : ""}`}
            onClick={() => setActiveTab("personel")}
          >
            Personel
          </button>
          <button
            className={`tab-button ${activeTab === "paylasilanlar" ? "active" : ""}`}
            onClick={() => setActiveTab("paylasilanlar")}
          >
            Payla≈üƒ±lanlar
          </button>
        </div>

        <div className="tab-content">
          {activeTab === "personel" && (
            <div>
              <h2>üìã Personel Hesaplarƒ±</h2>
              {error ? (
                <p className="error">{error}</p>
              ) : (
                <ul className="account-list">
                  {accounts.map((acc) => (
                    <li key={acc.id}>
                      <strong>{acc.username}</strong> - {acc.email} ({acc.ipAddress})
                    </li>
                  ))}
                </ul>
              )}
              {/* Buraya hesap ekle/g√ºncelle/sil ve payla≈üma i≈ülemleri gelecek */}
            </div>
          )}

          {activeTab === "paylasilanlar" && (
            <div>
              <h2>üë• Payla≈üƒ±lan Hesaplar</h2>
              <p>Burada sizinle payla≈üƒ±lan hesaplarƒ± g√∂receksiniz.</p>
              {/* Backend'den payla≈üƒ±lan hesaplar listesi eklenecek */}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
