import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/MainLayout.css";

function Home() {
  const [activeTab, setActiveTab] = useState("personel");
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [form, setForm] = useState({
    title: "",
    username: "",
    password: "",
    category: "",
    notes: "",
  });

  const fetchAccounts = async () => {
    try {
      const res = await axios.get("http://localhost:5059/api/accounts");
      setAccounts(res.data);
    } catch (err) {
      console.error("Veriler alınamadı:", err);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    try {
      await axios.post("http://localhost:5059/api/accounts", form);
      setForm({ title: "", username: "", password: "", category: "", notes: "" });
      fetchAccounts();
    } catch (err) {
      console.error("Ekleme hatası:", err);
    }
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5059/api/accounts/${selectedAccount.id}`, form);
      setForm({ title: "", username: "", password: "", category: "", notes: "" });
      setSelectedAccount(null);
      fetchAccounts();
    } catch (err) {
      console.error("Güncelleme hatası:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5059/api/accounts/${id}`);
      fetchAccounts();
    } catch (err) {
      console.error("Silme hatası:", err);
    }
  };

  const handleEdit = (account) => {
    setSelectedAccount(account);
    setForm({
      title: account.title,
      username: account.username,
      password: account.password,
      category: account.category,
      notes: account.notes,
    });
  };

  return (
    <div className="container mt-4">
      {/* Sekmeler */}
      <ul className="nav nav-tabs mb-3">
        <li className="nav-item">
          <button
            className={`nav-link ${activeTab === "personel" ? "active" : ""}`}
            onClick={() => setActiveTab("personel")}
          >
            Personel
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeTab === "paylasilan" ? "active" : ""}`}
            onClick={() => setActiveTab("paylasilan")}
          >
            Paylaşılanlar
          </button>
        </li>
      </ul>

      {/* Form */}
      <div className="card p-3 mb-3">
        <input name="title" value={form.title} onChange={handleInputChange} placeholder="Başlık" className="form-control mb-2" />
        <input name="username" value={form.username} onChange={handleInputChange} placeholder="Kullanıcı Adı" className="form-control mb-2" />
        <input name="password" value={form.password} onChange={handleInputChange} placeholder="Şifre" className="form-control mb-2" />
        <input name="category" value={form.category} onChange={handleInputChange} placeholder="Kategori" className="form-control mb-2" />
        <textarea name="notes" value={form.notes} onChange={handleInputChange} placeholder="Notlar" className="form-control mb-2" />

        <div className="d-flex gap-2">
          {selectedAccount ? (
            <button className="btn btn-success" onClick={handleUpdate}>Güncelle</button>
          ) : (
            <button className="btn btn-primary" onClick={handleAdd}>Ekle</button>
          )}
          <button className="btn btn-secondary" onClick={() => {
            setForm({ title: "", username: "", password: "", category: "", notes: "" });
            setSelectedAccount(null);
          }}>
            Temizle
          </button>
        </div>
      </div>

      {/* Liste */}
      <div className="row">
        {accounts
          .filter((a) => activeTab === "personel" ? true : a.shared === true)
          .map((account) => (
            <div key={account.id} className="col-md-4 mb-3">
              <div className="card p-3 shadow-sm">
                <h5>{account.title}</h5>
                <p><strong>Kullanıcı:</strong> {account.username}</p>
                <p><strong>Kategori:</strong> {account.category}</p>
                <p><strong>Notlar:</strong> {account.notes}</p>
                <div className="d-flex justify-content-between">
                  <button className="btn btn-warning btn-sm" onClick={() => handleEdit(account)}>Düzenle</button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDelete(account.id)}>Sil</button>
                  <button className="btn btn-info btn-sm">Paylaş</button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Home;
