import React, { useState } from 'react';
import axios from 'axios';

const AddAccountForm = ({ onAccountAdded }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    ipAddress: '',
    notes: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5059/api/accounts', formData);
      onAccountAdded(); // listeyi yenile
      setFormData({ username: '', password: '', email: '', ipAddress: '', notes: '' });
    } catch (error) {
      console.error('Kayıt eklenirken hata:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="username" value={formData.username} onChange={handleChange} placeholder="Kullanıcı Adı" required />
      <input name="password" value={formData.password} onChange={handleChange} placeholder="Şifre" />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="E-posta" />
      <input name="ipAddress" value={formData.ipAddress} onChange={handleChange} placeholder="IP Adresi" />
      <input name="notes" value={formData.notes} onChange={handleChange} placeholder="Notlar" />
      <button type="submit">Kaydet</button>
    </form>
  );
};

export default AddAccountForm;
